mapper.select:
select jd.*,u1.name 'owner_name',u2.name 'rec_by_name',u3.name 'edit_by_name',jf.family 'family',w.workcell 'workcell' from jig_definition jd left join jig_family jf on jd.family_id = jf.id left join workcell w on jd.workcell_id = w.id left join user u1 on jd.owner= u1.id left join user u2 on jd.rec_by = u2.id left join user u3 on jd.edit_by = u3.id
and jd.family_id = #{family_id}
and jd.code like concat('%',#{code},'%')
and jd.name like concat('%',#{name},'%')
and jd.user_for like concat('%',#{user_for},'%')
and jd.workcell_id = #{workcell_id}
limit #{page_number},#{page_size}

select count(*) from jig_definition jd left join jig_family jf on jd.family_id = jf.id left join workcell w on jd.workcell_id = w.id left join user u1 on jd.owner= u1.id left join user u2 on jd.rec_by = u2.id left join user u3 on jd.edit_by = u3.id
and jd.family_id = #{family_id}
and jd.code like concat('%',#{code},'%')
and jd.name like concat('%',#{name},'%')
and jd.user_for like concat('%',#{user_for},'%')
and jd.workcell_id = #{workcell_id}

mapper.update:
update jig_definition
name = #{jig_info.name}, code = #{jig_info.code}, model = #{jig_info.model},
part_no = #{jig_info.part_no},
family_id = #{jig_info.family_id}, workcell_id = #{jig_info.workcell_id}, upl = #{jig_info.upl}, user_for = #{jig_info.user_for}, pm_period = #{jig_info.pm_period}, edit_time = now(),edit_by = #{user_id}
where id = #{jig_info.id};
insert into operate(operate_man_id,operate_key_id,operate_time,table_name,field,type,old_value,new_value) values(#{user_id},#{jig_info.id},now(),'jig_definition',#{field},'update',#{old_value},#{new_value})

update purchase_income_submit
set status = #{status},first_time = now(),first_reason = #{first_reason},first_acceptor = #{user.id}
where id = #{id};

insert into operate(operate_man_id,operate_key_id,operate_time,table_name,field,type,old_value,new_value,operate_string)
values(#{user.id},#{id},now(),'purchase_income_submit',#{field},'update',#{old_value},#{new_value},
concat('监管员',#{user.name},'初审不通过~原因:',#{first_reason}))

update scrap_submit
set status = '1', first_acceptor = #{user.id},first_time = now(),first_reason = #{no_pass_reason}
where id = #{id};

insert into operate(operate_man_id,operate_key_id,operate_time,table_name,field,type,old_value,new_value,operate_string)
values(#{user.id},#{id},now(),'scrap_submit',#{field},'update',#{old_value},#{new_value},
concat('监管员',#{user.name},'初审不通过~原因:',#{no_pass_reason}))

mapper.select:
select s.*, u1.name submit_name, u2.name first_acceptor_name, u3.name final_acceptor_name from scrap_submit_history s INNER JOIN user u1 on u1.id = s.submit_id LEFT JOIN user u2 on u2.id = s.first_acceptor LEFT JOIN user u3 on u3.id = s.final_acceptor
u1.workcell_id = #{workcell_id}
and s.code like concat('%',#{code},'%')
and s.seq_id like concat('%',#{seq_id},'%')
and s.status = #{status}
and s.scrap_reason like concat('%',#{scrap_reason},'%')
and (STR_TO_DATE(s.submit_time,'%Y-%m-%d') BETWEEN '${start_date}' AND '${end_date}')
limit #{page_number},#{page_size}
